import"./modulepreload-polyfill-3cfb730f.js";document.addEventListener("DOMContentLoaded",()=>{const n=document.getElementById("github-token"),s=document.getElementById("save-token"),e=document.getElementById("token-status");chrome.storage.local.get(["githubToken"],t=>{t.githubToken&&(n.value=t.githubToken,e.textContent="Token loaded",e.className="status-message success")}),s.addEventListener("click",()=>{const t=n.value.trim();t.length>0?chrome.storage.local.set({githubToken:t},()=>{e.textContent="Token saved",e.className="status-message success"}):chrome.storage.local.remove(["githubToken"],()=>{e.textContent="Token deleted",e.className="status-message info"})})});
